import { AuthToken } from '../../../auth-client';
import { WebSocketConnection } from './connection-wrapper';

export abstract class BaseWebSocketService {
	constructor() {
		this.initialize();
	}

	initialize() {
		try {
			AuthToken.initInstanceStorage();
		} catch {}
	}
	protected getHeaders = async () => {
		const authHeaders: Record<string, string> = {};

		const token = await AuthToken.get();
		if (token) {
			authHeaders.Authorization = `Bearer ${token.access_token}`;
		}

		return authHeaders;
	};

	public abstract establish(): Promise<WebSocketConnection>;
}
