export type Chain = {
	/** ID in number form */
	id: number;
	/** Human-readable name */
	name: string;
	/** Internal network name */
	network: string;
	/** Currency used by chain */
	nativeCurrency: NativeCurrency;
	/** Flag for test networks */
	testnet?: boolean;
};

type NativeCurrency = {
	name: string;
	/** 2-6 characters long */
	symbol: string;
	decimals: number;
};
