import { auth } from '../../constants';
import { AUTH_TOKEN_KEY } from '../constants';
import { AuthTokenTypes, IAuthToken } from '../types/auth-token';
import { validateJwt } from '../utils';
import { LocalStorageBackend, StorageBackend } from '../utils/storage';

const AUTH_EXPIRY_BUFFER = 1 * 60 * -1; // 1 mins in seconds

class AuthToken extends IAuthToken {
	static _storageBackend: StorageBackend = null;
	private static token: auth.TMiraiAccessToken | undefined = null;

	constructor(opts?: auth.TMiraiAccessToken) {
		super(opts);
	}

	public static async init() {
		const client = new AuthToken();
		await client.initialize();

		return client;
	}

	private async initialize() {}

	static initInstanceStorage = () => {
		this._storageBackend = new LocalStorageBackend();
	};

	static save(token: auth.TMiraiAccessToken) {
		if (!this._storageBackend) {
			this._storageBackend = new LocalStorageBackend();
		}
		Promise.all([this._storageBackend.setItem(AUTH_TOKEN_KEY, JSON.stringify(token))]).then(() => {
			this.token = token;
		});
	}

	static async get(): Promise<auth.TMiraiAccessToken> {
		let token = this.token;

		if (!token) {
			const tokenFromStorage = JSON.parse(
				await this._storageBackend.getItem(AUTH_TOKEN_KEY),
			) as auth.TMiraiAccessToken;

			if (tokenFromStorage) {
				token = tokenFromStorage;
				this.token = token;
			}
		}

		return token;
	}

	static async isValid(buffer: number = AUTH_EXPIRY_BUFFER): Promise<boolean> {
		try {
			return validateJwt(this.token.access_token, buffer);
		} catch (e) {}
		return false;
	}

	static async isValidRefreshToken(): Promise<boolean> {
		try {
			return validateJwt(this.token.refresh_token);
		} catch (e) {}
		return false;
	}
}

export { AuthToken };
