import _ from 'lodash';

export class ServiceQueryRequest {
	private _url: string;
	private _params: string;

	constructor(url: string, params: string) {
		this._url = url;
		this._params = params;
	}

	addParam(key: string, value: string) {
		key = encodeURIComponent(key);
		value = encodeURIComponent(value);

		// kvp looks like ['key1=value1', 'key2=value2', ...]
		var kvp = this._params.substr(1).split('&');

		kvp = kvp.filter((e) => e !== '');

		let i = 0;

		for (; i < kvp.length; i++) {
			if (kvp[i].startsWith(key + '=')) {
				let pair = kvp[i].split('=');
				pair[1] = value;
				kvp[i] = pair.join('=');
				break;
			}
		}

		if (i >= kvp.length) {
			kvp[kvp.length] = [key, value].join('=');
		}

		if (kvp.length === 1) {
			let params = kvp[0];
			this._params = params;
			return;
		}

		// can return this or...
		let params = kvp.join('&');
		this._params = params;
	}

	toUri() {
		return this._url + '?' + this._params;
	}
}
