import { BaseHttpService } from './http-base';

const PAIRING_URL: string = 'http://localhost:3000';

class MiraiIDAPI extends BaseHttpService {
	public static readonly INSTANCE = new MiraiIDAPI();

	private constructor() {
		super(PAIRING_URL);
	}

	async getPairingUri(topicId: string, { accessToken }: { accessToken: string }): Promise<string> {
		return this.sendGet<string>(
			'/get-pairing',
			{
				topicId,
			},
			{
				accessToken,
			},
		);
	}
}

export { MiraiIDAPI };
