import { WalletClient } from '../../clients/wallet-client';

export type SignMessageArgs = {
	/** Message to sign with wallet */
	message: string;
};

export type SignMessageReturnType = string;

export type SignMessageResult = SignMessageReturnType;

export async function signMessage(client: WalletClient, args: SignMessageArgs): Promise<SignMessageResult> {
	return client.request({
		method: 'personal_sign',
		params: [args.message, client.account],
	});
}
