import { auth } from '../../constants';
import { SignerWebSocketService } from '../transports/websocket/ws-signer';

export type ConnectionOpts = {
	auth?: auth.TMiraiToken;
};

export const CONNECTION_EVENTS = {
	init: 'ws_init',
	connected: 'ws_connected',
	open: 'ws_open',
	open_error: 'ws_open_error',
	register_error: 'ws_register_error',
	disconnected: 'ws_disconnected',
	error: 'ws_error',
};

export abstract class Connection {
	public abstract ws: InstanceType<typeof SignerWebSocketService>;

	public abstract isConnected(): boolean;
	public abstract setAuthToken(token: auth.TMiraiToken): void;
	constructor(opts: ConnectionOpts) {}
}
