export namespace env {
	export const MIRAI_ID_API_URL_DEV = 'https://id-dev-v2.mirailabs.co';
	export const MIRAI_ID_API_URL_PROD = 'https://id.miraiid.io';
}

export interface BaseApiResponse<T> {
	statusCode: number;
	message: string;
	data: T;
	status?: boolean;
}

export type Mode = 'development' | 'production';

export namespace auth {
	export type TMiraiAccessToken = {
		id_token?: string;
		access_token: string;
		expires_in: number;
		refresh_expires_in: number;
		refresh_token: string;
		type?: string;
		sessionState?: string;
		scope?: string;
	};

	export type TMiraiToken = {
		access_token: string;
		expires_in?: number;
		refresh_expires_in?: number;
		refresh_token: string;
		type?: string;
		sessionState?: string;
		scope?: string;
	};

	export type TMiraiUserData = {
		firstName: string;
		lastName: string;
		sub: string;
		id: string;
		emailVerified: true;
		emailVerifiedAt: string;
		givenName: string;
		familyName: string;
		exp: number;
		azp: string;
		status: boolean;
		username: string;
		email_verified: true;
		createdAt: string;
		updatedAt: string;
		email: string;
		socials?: TUserSocial[] | undefined;
	};

	export type TUserSocial = {
		id: string;
		createdAt: string;
		updatedAt: string;
		type: SocialType;
		provider: SocialType;
		socialId: string;
		email: string;
		picture: unknown;
		metadata?: TUserSocialMetadata;
		username: string;
	};

	type SocialType = 'facebook' | 'google';

	export type TUserSocialMetadata = {
		sub: string;
		name: string;
		email: string;
		locale: string;
		picture: string;
		provider: string;
		given_name: string;
		family_name: string;
		email_verified: boolean;
	};

	export type TAuthUrl = {
		url: string;
		expiredTime: number;
	};

	export type TSocialPicture = {
		data: TSocialPictureData;
	};

	export type TSocialPictureData = {
		height: number;
		url: string;
		is_silhouette: boolean;
		width: number;
	};
}

export type GrantType = 'authorization_code' | 'refresh_token';

export type TMiraiAuthenTokenRequest = {
	code: string;
	state: string;
	redirect_uri: string;
	code_verifier: string;
	client_id: string;
	client_secret: string;
	scope: string;
	grant_type: GrantType;
	refresh_token?: string;
};

export const AUTH_TOKEN_KEY = 'mirai_auth_access_token_key';

export type Channel = 'LOGIN';
export type State = 'START' | 'END' | 'RECEIVED';
