import { EventEmitter } from 'events';
import {
	IEthereumProvider as IProvider,
	IEthereumProviderEvents,
	ProviderAccounts,
	RequestArguments,
	ProviderChainId,
	RpcMethod,
	IProviderEvents,
} from './types';
import { Connection } from '../connection/connection';
import { miraiTestnet } from '../../constants/chains';
import { NotFoundMiraiConnectionError } from '../errors/NoConnectionError';
import { MiraiIDAPI } from '../transports/http/http-miraiid';

export type RpcEvent = 'accountsChanged' | 'chainChanged' | 'message' | 'disconnect' | 'connect';

export interface EthereumRpcMap {
	[chainId: string]: string;
}

export interface SessionEvent {
	event: { name: string; data: any };
	chainId: string;
}

export interface ConnectOps {
	chains?: string[];
	optionalChains?: string[];
	rpcMap?: EthereumRpcMap;
	pairingTopic?: string;
	chainId?: string;
}

export interface IEthereumProvider extends IProvider {
	connect(opts?: ConnectOps | undefined): Promise<void>;
}

export function getEthereumChainId(chains: string[]): string {
	return chains[0].split(':')[1];
}

export function toHexChainId(chainId: string): string {
	return chainId;
}

// helper type to force setting at least one value in an array
type ArrayOneOrMore<T> = {
	0: T;
} & Array<T>;

export type EthereumProviderOptions = {
	connection?: Connection;
	chainId: string;
};

export type WalletConnectSession = {
	topic: string;
	expiry: number;
	acknowledged: boolean;
	pairingTopic: string;
	controller: string;
	peer: {
		publicKey: string;
		metadata: {
			name: string;
			description: string;
			url: string;
			icons: [];
		};
	};
	namespaces?: {
		'eip155': {
			accounts: string[];
			events: string[];
		};
	};
};

export class MiraiProvider implements IEthereumProvider {
	public emitter = new EventEmitter();
	public accounts: string[] = [];
	private connection: Connection;
	public chainId = `0x${miraiTestnet.id.toString(16)}`;

	constructor() {
		// assigned during initialize
		// this._signer = new SignerWebSocketService();
	}

	static async init(opts: EthereumProviderOptions): Promise<MiraiProvider> {
		const provider = new MiraiProvider();
		await provider.initialize(opts);

		return provider;
	}

	// public async request<T = unknown>(args: RequestArguments<RpcMethod>): Promise<T> {
	// 	return new Promise(async (resolve, reject) => {
	// 		const socket = await this.connection.ws.establish(true);

	// 		socket.on('exception', (args: any) => {
	// 			socket.disconnect();
	// 			reject(new Error(JSON.stringify(args)));
	// 		});

	// 		socket.on('connect', () => {
	// 			socket
	// 				.getSocket()
	// 				.emit(this.connection.topicId, args, (data: T & { code: number; message: string }) => {
	// 					if (data.code) {
	// 						reject(new Error(data.message));
	// 					}

	// 					resolve(data);
	// 				});
	// 		});

	// 		socket.on('connect_error', () => {
	// 			reject(new Error('Connection error'));
	// 		});

	// 		socket.connect();
	// 	});
	// }
	public async request<T = unknown>(args: RequestArguments<RpcMethod>): Promise<T> {
		return await MiraiIDAPI.INSTANCE.sendRequest(
			this.connection.wcTopicId,
			{
				chainId: `${this.connection.namespace}:${Number(parseInt(this.chainId, 16))}`,
				request: args,
			},
			this.connection.accessToken,
		);
	}

	public async enable(): Promise<ProviderAccounts> {
		const accounts = await this.request<ProviderAccounts>({ method: 'eth_requestAccounts' });
		return accounts as ProviderAccounts;
	}

	public async getChainId(): Promise<ProviderChainId> {
		const chain = await this.request({ method: 'eth_chainId' });

		return chain as ProviderChainId;
	}

	public async connect(opts?: ConnectOps): Promise<void> {
		if (!this.connection.isConnected()) {
			throw new Error('Connection not initialized. Call MiraiConnection.init() first');
		}

		this.emitter.emit('connect', { chainId: this.chainId });
	}

	public on: IEthereumProviderEvents['on'] = (event, listener) => {
		this.emitter.on(event, listener);
		return this;
	};

	public once: IEthereumProviderEvents['once'] = (event, listener) => {
		this.emitter.once(event, listener);
		return this;
	};

	public removeListener: IEthereumProviderEvents['removeListener'] = (event, listener) => {
		this.emitter.removeListener(event, listener);
		return this;
	};

	public off: IEthereumProviderEvents['off'] = (event, listener) => {
		this.emitter.off(event, listener);
		return this;
	};

	get isWalletConnect() {
		return true;
	}

	protected switchEthereumChain(chainId: number): void {
		this.request({
			method: 'wallet_switchEthereumChain',
			params: [{ chainId: chainId.toString(16) }],
		});
	}

	protected parseChainId(chainId: string): number {
		return Number(chainId.split(':')[1]);
	}

	protected setChainIds(chains: string[]) {
		this.emitter.emit('chainChanged', this.chainId);
		this.persist();
	}

	protected setChainId(chain: number) {
		this.switchEthereumChain(chain);
	}

	protected parseAccountId(account: string): { chainId: string; address: string } {
		const [namespace, reference, address] = account.split(':');
		const chainId = `${namespace}:${reference}`;
		return { chainId, address };
	}

	protected setAccounts(accounts: string[]) {
		this.accounts = accounts.map((x) => this.parseAccountId(x).address);

		this.emitter.emit('accountsChanged', this.accounts);
	}

	protected async initialize(opts: EthereumProviderOptions) {
		this.connection = opts.connection;
		this.chainId = opts.chainId;
		if (!this.connection) {
			throw new NotFoundMiraiConnectionError('No mirai connection found');
		}

		// await this.registerEventListener();
	}

	// private async registerEventListener(): Promise<void> {
	// 	return new Promise(async (resolve, reject) => {
	// 		const socket = await this.connection.ws.establish(true);

	// 		socket.on('exception', (args: any) => {
	// 			socket.disconnect();
