	// 			resolve();
	// 		});

	// 		socket.on(
	// 			this.connection.topicId,
	// 			(
	// 				payload: IProviderEvents.BaseEventArgs<{
	// 					event: { name: string; data: any };
	// 					chainId: string;
	// 				}>,
	// 			) => {
	// 				const { params } = payload;

	// 				if (params && params !== undefined) {
	// 					const { event } = params;
	// 					if (event.name === 'accountsChanged') {
	// 						this.accounts = this.parseAccounts(event.data);
	// 						this.emitter.emit('accountsChanged', this.accounts);
	// 					} else if (event.name === 'chainChanged') {
	// 						this.setChainId(event.data);
	// 					} else {
	// 						this.emitter.emit(event.name as any, event.data);
	// 					}
	// 				}
	// 			},
	// 		);

	// 		socket.on('connect_error', () => {
	// 			reject(new Error('Connection error'));
	// 		});

	// 		socket.connect();
	// 	});
	// }

	protected reset() {
		this.chainId = `0x${miraiTestnet.id.toString(16)}`;
		this.accounts = [];
	}

	protected persist() {}

	protected parseAccounts(payload: string | string[]): string[] {
		if (typeof payload === 'string' || payload instanceof String) {
			return [this.parseAccount(payload)];
		}
		return payload.map((account: string) => this.parseAccount(account));
	}

	protected parseAccount = (payload: any): string => {
		return this.parseAccountId(payload).address;
	};
}
