import EventEmitter from 'eventemitter3';
import { SignerWebSocketService } from '../transports/websocket/ws-signer';
import { CONNECTION_EVENTS, ConfigEvents, Connection, ConnectionOpts } from './connection';
import { v4 as uuidv4 } from 'uuid';
import { TConnectionEvent } from './types';
import { Chain } from 'src/constants/chains';

// export const MiraiRelayerUrl: string = 'wss://id-api-dev.mirailabs.co/mpc';

class MiraiConnection extends Connection {
	public topicId: string;

	// FOR PROVIDER
	public chains: Chain[];
	public namespace: string;

	private readonly miraiRelayerUrl: string = 'ws://localhost:3000';

	private pending = false;
	private initializing = false;
	private initialized = false;

	// FOR WS CONNECTION
	public accessToken: string;
	public ws: InstanceType<typeof SignerWebSocketService>;

	constructor(opts: ConnectionOpts) {
		super(opts);

		this.accessToken = opts.accessToken;
	}

	public static async init({ accessToken }: { accessToken?: string } = {}) {
		const connection = new MiraiConnection({ accessToken });

		await connection.initialize();

		return connection;
	}

	// public on(event: TConnectionEvent, listener: any) {
	// 	this.events.on(event, listener);
	// }

	// public once(event: TConnectionEvent, listener: any) {
	// 	this.events.once(event, listener);
	// }

	// public off(event: TConnectionEvent, listener: any) {
	// 	this.events.off(event, listener);
	// }

	// public removeListener(event: TConnectionEvent, listener: any) {
	// 	this.events.removeListener(event, listener);
	// }

	public isConnected(): boolean {
		return this.initialized;
	}

	// PRIVATE
	private async initialize(): Promise<void> {
		if (this.pending) {
			return new Promise((resolve, reject) => {
				this.once('open', () => {
					this.once('open_error', (error: any) => {
						reject(error);
					});
					if (typeof this.ws === 'undefined') {
						return reject(new Error('WS not initialized'));
					}
					resolve();
				});
			});
		}

		try {
			this.pending = true;
			await this._registerListener();
			await this.register(this.accessToken);
			const connected = await this.connect({ accessToken: this.accessToken });

			this.emit('open');

			if (connected) {
				this.initialized = true;
			}
		} catch (e) {
			this.emit('open_error', e);
			throw e;
		}
	}

	private async register(accessToken: string): Promise<SignerWebSocketService> {
		if (typeof this.ws !== 'undefined') {
			return this.ws;
		}

		if (this.initializing) {
			return new Promise((resolve, reject) => {
				this.once('register_error', (error: any) => {
					reject(error);
				});
				this.once('init', () => {
					if (typeof this.ws === 'undefined') {
						return reject(new Error('WS not initialized'));
					}
					resolve(this.ws);
				});
			});
		}
		try {
			this.initializing = true;

			this.ws = new SignerWebSocketService({
				url: this.miraiRelayerUrl,
				accessToken,
			});

			console.log('this.ws', this.ws);

			this.initializing = false;
			this.emit('init');
			return this.ws;
		} catch (e) {
			this.emit('register_error', e);
			throw e;
		}
	}

	public async connect({ accessToken }: { accessToken: string }): Promise<boolean> {
		return new Promise(async (resolve, reject) => {
			try {
				if (!this.ws || this.ws === undefined) {
					await this.register(accessToken);
				}
				const socket = await this.ws.establish(true);

				socket.on('exception', (args: any) => {
					socket.disconnect();
					this.emit('error', new Error(JSON.stringify(args)));

					reject(false);
				});

				socket.on('connect', (e: any) => {
					this.emit('connected', uuidv4());

					resolve(true);
				});

				socket.on('connect_error', () => {
					this.emit('error', new Error(JSON.stringify('Connection error')));
					reject(false);
				});

				socket.connect();
			} catch (error) {
				console.error(error);
				throw error;
			} finally {
			}
		});
	}

	public async disconnect(): Promise<void> {
		return new Promise(async (resolve, reject) => {
			try {
				const socket = await this.ws.establish(true);

				socket.on('exception', (args: any) => {
					socket.disconnect();
					this.emit('open_error', new Error(JSON.stringify(args)));

					reject(new Error(JSON.stringify(args)));
				});

				console.time('WS get-pail');
				socket.on('connect', () => {
					console.log('connected');
				});

				socket.on('connect_error', () => {
					reject(new Error('Connection error'));
				});

				socket.disconnect();
			} catch (error) {
				console.error(error);
				throw error;
			} finally {
			}
		});
	}

	private async _registerListener() {
		this.once('namespace', (np: string) => {
			this.namespace = np;

			console.log('namespace', np);
		});
		this.once('chains', (chains: Chain[]) => {
			console.log('chains', chains);
			this.chains = chains;
		});
	}
}

export { MiraiConnection };
