import { SignerWebSocketService } from '../transports/websocket/ws-signer';
import { Connection, ConnectionOpts } from './connection';
import { v4 as uuidv4 } from 'uuid';
import { MiraiProvider, WalletConnectSession } from '../providers/mirai-provider';

// export const MiraiRelayerUrl: string = 'wss://id-api-dev.mirailabs.co/mpc';

class MiraiConnection extends Connection {
	public topicId: string;

	// FOR PROVIDER
	public chains: string[];
	public namespace: string;

	private readonly miraiRelayerUrl: string = 'ws://localhost:3000';

	private pending = false;
	private initializing = false;
	private initialized = false;
	private isApproved: boolean = false;

	// FOR WS CONNECTION
	public accessToken: string;
	public ws: InstanceType<typeof SignerWebSocketService>;

	constructor(opts: ConnectionOpts) {
		super(opts);

		this.accessToken = opts.accessToken || null;
	}

	public static async init({ accessToken }: { accessToken?: string } = {}) {
		const connection = new MiraiConnection({ accessToken });

		await connection.initialize();

		return connection;
	}

	// public on(event: TConnectionEvent, listener: any) {
	// 	this.events.on(event, listener);
	// }

	// public once(event: TConnectionEvent, listener: any) {
	// 	this.events.once(event, listener);
	// }

	// public off(event: TConnectionEvent, listener: any) {
	// 	this.events.off(event, listener);
	// }

	// public removeListener(event: TConnectionEvent, listener: any) {
	// 	this.events.removeListener(event, listener);
	// }

	public async getProvider(): Promise<MiraiProvider> {
		if (this.isApproved) {
			return await MiraiProvider.init({
				connection: this,
			});
		}
		return null;
	}

	public isConnected(): boolean {
		return this.initialized;
	}

	// PRIVATE
	private async initialize(): Promise<void> {
		if (this.pending) {
			return new Promise((resolve, reject) => {
				this.once('open', () => {
					this.once('open_error', (error: any) => {
						reject(error);
					});
					if (typeof this.ws === 'undefined') {
						return reject(new Error('WS not initialized'));
					}
					resolve();
				});
			});
		}

		try {
			this.pending = true;
			await this._registerListener();

			if (this.accessToken) {
				await this.register(this.accessToken);
			}

			this.emit('open');
		} catch (e) {
			this.emit('open_error', e);
			throw e;
		}
	}

	private async register(accessToken: string): Promise<SignerWebSocketService> {
		if (typeof this.ws !== 'undefined') {
			return this.ws;
		}

		if (this.initializing) {
			return new Promise((resolve, reject) => {
				this.once('register_error', (error: any) => {
					reject(error);
				});
				this.once('init', () => {
					if (typeof this.ws === 'undefined') {
						return reject(new Error('WS not initialized'));
					}
					resolve(this.ws);
				});
			});
		}
		try {
			this.initializing = true;

			this.ws = new SignerWebSocketService({
				url: this.miraiRelayerUrl,
				accessToken,
			});

			console.log('this.ws', this.ws);

			this.initializing = false;
			this.emit('init');
			return this.ws;
		} catch (e) {
			this.emit('register_error', e);
			throw e;
		}
	}

	public async connect({ accessToken }: { accessToken: string }): Promise<boolean> {
		return new Promise(async (resolve, reject) => {
			try {
				if (!this.ws || this.ws === undefined) {
					console.log('Register new connection with', accessToken);
					await this.register(accessToken);
				}
				const socket = await this.ws.establish(true);

				socket.on('exception', (args: any) => {
					socket.disconnect();
					this.emit('error', new Error(JSON.stringify(args)));

					reject(false);
				});

				socket.on('connect', (e: any) => {
					const topicId = uuidv4();
					this.emit('connected', topicId);
					this.accessToken = accessToken;

					this.initialized = true;

					socket.getSocket().on(topicId, (resp: { method: string; data: any }) => {
						if (resp.method === 'rejected') {
							this.emit('rejected', new Error(JSON.stringify(resp.data)));
							this.isApproved = false;

							if (typeof window !== 'undefined' && window !== undefined) {
								window.postMessage({ topicId, message: 'user_rejected' }, '*');
							}

							reject(false);
						}

						this.isApproved = true;

						this.emit('approved', {
							...(resp.data as WalletConnectSession),
							topicId: topicId,
						});

						if (typeof window !== 'undefined' && window !== undefined) {
							window.postMessage({ topicId, message: 'user_approved' }, '*');
						}

						resolve(true);
					});

					resolve(true);
				});

				socket.on('connect_error', () => {
					this.emit('error', new Error(JSON.stringify('Connection error')));
					reject(false);
				});

				socket.connect();
			} catch (error) {
				console.error(error);
				throw error;
			} finally {
			}
		});
	}

	public async disconnect(): Promise<boolean> {
		return new Promise((resolve, reject) => {
			if (this.ws.socket.disconnect()) {
				this.emit('disconnected');
				resolve(true);
			}

			resolve(false);
		});
	}

	private async resetAll(): Promise<void> {
		await this.disconnect();

		this.removeAllListeners();

		this.accessToken = null;
		this.chains = [];
		this.initialized = false;
		this.namespace = null;
	}

	private async _registerListener() {
		this.once('connected', async (topic: string) => {
			console.log('topic', topic);
			this.topicId = topic;

			if (typeof window !== 'undefined' && window !== undefined) {
				window.addEventListener('message', async (event) => {
					const { data } = event;

					const { topicId, message } = data;

					console.log('data', data, this.topicId, message);

					if (topicId === this.topicId && 'window_closed' === message) {
						await this.resetAll();
					}
				});
			}
		});

		this.once('namespace', (namespace: string) => {
			this.namespace = namespace;

			console.log('namespace', namespace);
		});

		this.once('chains', (chains: string[]) => {
			console.log('chains', chains);
			this.chains = chains;
		});
	}
}

export { MiraiConnection };
