import { IAuthClient } from './client';

export declare namespace AuthEngineTypes {
	type RequestParams = {
		redirect_uri: string;
		code_challenge_method: string;
		state?: string;
		code_challenge?: string;
		scope?: string;
		origin?: string;
	};
}

export abstract class IAuthEngine {
	constructor(public client: IAuthClient) {}

	public abstract init(): void;

	public abstract registerOnAuthResponse(): Promise<void>;

	public abstract request(params: AuthEngineTypes.RequestParams): Promise<void>;

	// ---------- Protected Helpers --------------------------------------- //

	protected abstract setExpiry(topic: string, expiry: number): Promise<void>;

	protected abstract onAuthRequest(): Promise<void>;

	protected abstract onAuthResponse(): void;
}
