import EventEmitter from 'eventemitter3';
import { Chain } from '../../constants/chains';
import { SignerWebSocketService } from '../transports/websocket/ws-signer';
import { TConnectionEvent } from './types';

export type ConnectionOpts = {
	accessToken?: string;
};

export const CONNECTION_EVENTS = {
	init: 'init',
	connected: 'connected',
	open: 'open',
	open_error: 'open_error',
	register_error: 'register_error',
	disconnected: 'disconnected',
	error: 'error',
};

export type ConfigEvents = 'namespace' | 'chains';

export abstract class Connection extends EventEmitter<TConnectionEvent | ConfigEvents> {
	public abstract ws: InstanceType<typeof SignerWebSocketService>;

	public abstract isConnected(): boolean;
	public abstract topicId: string;
	public abstract chains: Chain[];
	public abstract namespace: string;

	constructor(opts: ConnectionOpts) {
		super();
	}
}
